import java.lang.invoke.MethodHandles
import java.lang.invoke.MethodType
import java.lang.invoke.WrongMethodTypeException

class C {
    fun string(x: String): Any? = x

    fun varargAny(vararg y: Any?): Any? = y[0]
}

fun box(): String {
    val cstr = MethodHandles.lookup().findVirtual(C::class.java, "string", MethodType.methodType(Any::class.java, String::class.java))

    val refstr = cstr::invokeExact
    try {
        refstr.invoke(arrayOf("Hmm"))
        return "Fail"
    } catch (e: WrongMethodTypeException) {
        // OK
    }

    val cva = MethodHandles.lookup().findVirtual(C::class.java, "varargAny", MethodType.methodType(Array<Any>::class.java, Any::class.java))
    val refva = cva::invokeExact

    return refva.invoke(arrayOf("OK")) as String
}

