fun CharSequence.withReplace(startOffset: Int, endOffset: Int, replacementText: CharSequence): String
{
    return "${subSequence(0,startOffset)}$replacementText${subSequence(endOffset,length)}"
}

public fun CharSequence.replaceRange(startIndex: Int, endIndex: Int, replacement: CharSequence): CharSequence {
    if (endIndex < startIndex)
        throw IndexOutOfBoundsException("End index ($endIndex) is less than start index ($startIndex).")
    val sb = StringBuilder()
    sb.append(this, 0, startIndex)
    sb.append(replacement)
    sb.append(this, endIndex, length)
    return sb
}

fun CharSequence.withReplace(startOffset: Int, endOffset: Int, replacementText: CharSequence): CharSequence
{
    return "${subSequence(0,startOffset)}$replacementText${subSequence(endOffset,length)}" as CharSequence
}

